import React from "react";
import { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

function RegisterPage() {
  const [getError, setError] = useState(null);
  const [getStyles, setStyles] = useState([
    "1px solid black",
    "1px solid black",
    "1px solid black",
  ]);
  const usernameRef = useRef();
  const passOneRef = useRef();
  const passTwoRef = useRef();
  const nav = useNavigate();

  function registerUser() {
    const form = {
      username: usernameRef.current.value,
      pass: passOneRef.current.value,
    };
    let styles = [...getStyles];

    if (form.username.length < 3 || form.username.length > 20) {
      styles[0] = "2px solid red";
      setStyles(styles);
      return setError("Bad username");
    }

    if (form.passTwo !== form.passOne) {
      styles[2] = "2px solid red";
      setStyles(styles);
      return setError("bad password");
    }

    if (form.passTwo.length < 3 || form.passTwo.length > 20) {
      styles[2] = "2px solid red";
      setStyles(styles);
      return setError("bad password");
    }

    setStyles([
      "1px solid black",
      "1px solid black",
      "1px solid black",
      "1px solid black",
    ]);

    setError(null);
  }

  return (
    <div>
      <input ref={usernameRef} type="text" placeholder="username" />
      <input ref={passOneRef} type="text" placeholder="pass 1" />
      <input ref={passTwoRef} type="text" placeholder="pass 2" />
      {getError && <h1>{getError}</h1>}

      <button onClick={registerUser}>Register</button>
    </div>
  );
}

export default RegisterPage;
