import { useState } from "react";
import "./App.css";

export default function App() {
  const [values, setValues] = useState({
    username: "",
    email: "",
    pass1: "",
    pass2: "",
    location: "",
  });
  const [error, setError] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (error.includes(name)) {
      setError(error.filter((e) => e !== name));
    }
    setValues({ ...values, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { username, email, pass1, pass2, location } = values; //can add rest according to your requirement
    const errorList = [];
    if (!username) {
      errorList.push("username");
    }
    if (!email) {
      errorList.push("email");
    }
    if (!pass1) {
      errorList.push("pass1");
    }
    if (!pass2) {
      errorList.push("pass2");
    }
    if (!location) {
      errorList.push("location");
    }
    console.log(errorList);
    setError(errorList);
  };
  console.log(error);
  return (
    <div className="App">
      <div className="container">
        <div className="form">
          <input
            type="text"
            placeholder="username"
            name="username"
            value={values.username}
            onChange={handleInputChange}
            className={error.includes("username") ? "error" : ""}
          />
          <input
            type="email"
            placeholder="email"
            name="email"
            value={values.email}
            onChange={handleInputChange}
            className={error.includes("email") ? "error" : ""}
          />
          <input
            type="text"
            placeholder="password"
            name="pass1"
            value={values.pass1}
            onChange={handleInputChange}
            className={error.includes("pass1") ? "error" : ""}
          />
          <input
            type="text"
            placeholder="confirm password"
            name="pass2"
            value={values.pass2}
            onChange={handleInputChange}
            className={error.includes("pass2") ? "error" : ""}
          />
          <select
            value={values.location}
            name="location"
            onChange={handleInputChange}
          >
            <option value="Vilnius">Vilnius</option>
            <option value="Kaunas">Kaunas</option>
            <option value="Klaipeda">Klaipeda</option>
            <option value="Siauliai">Siauliai</option>
            <option value="Trakai">Trakai</option>
          </select>
          <button onClick={handleSubmit}>Validate</button>
        </div>
      </div>
    </div>
  );
}
