import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateArr } from "../features/users";
import { useContext } from "react";
import mainContext from "../context/mainContext";
import { useState } from "react";

function SingleUser({ box, index }) {
  const { setInput, input } = useContext(mainContext);
  const useriai = useSelector((state) => state.users.value);
  const dispatch = useDispatch();
  const [selected, setSelected] = useState(false);

  function activeBox() {
    setInput(true);
    setSelected(true);
  }
  return (
    <div
      style={{ background: selected ? "grey" : "" }}
      onClick={activeBox}
      className="onebox"
    ></div>
  );
}

export default SingleUser;
