import React from "react";
import { useState } from "react";
import Question from "../components/Question";

function GamePage() {
  const [gameState, setGameState] = useState("start");
  const [question, setQuestion] = useState(null);
  function startGame() {
    fetch("http://localhost:4000/start")
      .then((res) => res.json())
      .then((data) => {
        setQuestion(data.question);
        setGameState("game");
      });
  }

  function answear(correctIndex) {
    console.log(correctIndex);
  }

  const qq = {
    question: "What is the scientific name of a butterfly?",
    answers: ["Apis", "Coleoptera", "Formicidae", "Rhopalocera"],
    correctIndex: 3,
  };
  return (
    <div className="start">
      {gameState === "start" && <button onClick={startGame}>START GAME</button>}
      {gameState === "game" && <Question item={qq} answear={answear} />}
    </div>
  );
}

export default GamePage;
