import "./App.css";

import { useState, useEffect, useRef } from "react";
import movies from "./assets/movies.json";
import Movie from "./components/Movie";
import Modal from "./components/Modal";
import FavoritePage from "./components/FavModal";

function App() {
  const [getModal, setModal] = useState(false);
  const [getModalItem, setModalItem] = useState([]);
  const [getFavorites, setFavorites] = useState([]);
  const [getShowFav, setShowFav] = useState(false);

  const slicedArray = movies.slice(0, 20);

  function showFav() {
    setShowFav(true);
  }

  return (
    <div className="App">
      <div className="fav">
        <button onClick={showFav}>FAVORITES ({getFavorites.length})</button>
      </div>
      <div className="allmovies">
        {slicedArray.map((x, i) => (
          <Movie
            getModal={getModal}
            setModal={setModal}
            key={i}
            item={x}
            setModalItem={setModalItem}
          />
        ))}

        {getModal && (
          <Modal
            setModal={setModal}
            item={getModalItem}
            setFavorites={setFavorites}
            getFavorites={getFavorites}
          />
        )}
        {getShowFav && (
          <FavoritePage getFavorites={getFavorites} setShowFav={setShowFav} />
        )}
      </div>
    </div>
  );
}

export default App;
