import React from "react";

function SingleCell({ item, selected, index, setSelected }) {
  let template;
  const myClassName = selected === index ? "selected cell" : "cell";

  if (item) {
    template = (
      <div className={myClassName} style={{ backgroundColor: item.color }}>
        {item.name}
      </div>
    );
  } else {
    template = <div className={myClassName}></div>;
  }

  return <div onClick={() => setSelected(index)}>{template}</div>;
}

export default SingleCell;
