import React from "react";
import Picture from "./Picture";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function Comp1() {
  const { getImage, togle } = useContext(mainContext);
  let bgColor = {
    flexDirection: togle ? "column" : "row",
    flexWrap: togle ? "" : "wrap",
  };

  return (
    <div className="comp1" style={bgColor}>
      {getImage.map((x) => (
        <Picture object={x} />
      ))}
    </div>
  );
}

export default Comp1;
