import React from "react";
import { useState } from "react";

function Box({ id, color, setItems, getItems }) {
  function changecolor(id) {
    const newarr = [...getItems];

    newarr.forEach((x, i) => {
      if (x.id === Number(id)) {
        x.color = "black";
      } else {
        x.color = "white";
      }
    });
    setItems(newarr);
  }

  return (
    <div
      id={id}
      onClick={() => changecolor(id)}
      style={{ background: color }}
      className="box a"
    ></div>
  );
}

export default Box;
