import React from "react";
import { useContext } from "react";
import mainContext from "../context/mainContext";
import { useRef } from "react";

function Comp3() {
  const inColor = useRef();
  const { setColor } = useContext(mainContext);

  function setingColor() {
    setColor(inColor.current.value);
  }

  return (
    <div className="comp3">
      <input ref={inColor} type="text" placeholder="cards color" />
      <button onClick={setingColor}>SET</button>
    </div>
  );
}

export default Comp3;
