import { useState, useRef } from "react";

const Footer = ({ getUser, setUser, getButton }) => {
  let inName = useRef();
  let inGender = useRef();
  let inPhoto = useRef();

  function addUser() {
    const user = {
      name: inName.current.value,
      gender: inGender.current.value,
      image: inPhoto.current.value,
    };
    setUser([...getUser, user]);
    console.log(getUser);
  }

  return (
    <div className="form">
      <input ref={inPhoto} type="url" placeholder="photo url" />
      <input ref={inName} type="text" placeholder="username" />
      <select ref={inGender} name="gender">
        <option value="" disabled hidden>
          SELECT GENDER
        </option>
        <option value="Male">Male</option>
        <option value="Female">Female</option>
      </select>
      <button onClick={addUser} style={{ display: getButton }}>
        Add user
      </button>
    </div>
  );
};

export default Footer;
