import React from "react";
import { useRef, useState } from "react";

function CreateReservation({ selected, setTable }) {
  const nameRef = useRef();
  const colorRef = useRef();
  const [error, setError] = useState(null);
  function action() {
    const values = {
      name: nameRef.current.value,
      color: colorRef.current.value,
      selected,
    };

    const options = {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(values),
    };

    fetch("http://localhost:4000/addReservation/", options)
      .then((res) => res.json())
      .then((data) => {
        if (!data.error) {
          setError(null);
          setTable(data.table);
        } else {
          setError(data.message);
        }
      });
  }
  return (
    <div className="d-flex flex-column p-10">
      <input ref={nameRef} type="text" placeholder="username" />
      <input ref={colorRef} type="text" placeholder="color" />
      {error && <div>{error}</div>}

      <button onClick={action}> Make Reservation</button>
    </div>
  );
}

export default CreateReservation;
