import "./App.css";

import { useState, useEffect, useRef } from "react";
import movies from "./assets/movies.json";
import Movie from "./components/Movie";
import Modal from "./components/Modal";

function App() {
  const [getModal, setModal] = useState("none");
  const [getModalItem, setModalItem] = useState([]);

  const slicedArray = movies.slice(0, 50);
  console.log(movies[0]);
  return (
    <div className="App">
      <div className="allmovies">
        {slicedArray.map((x, i) => (
          <Movie
            getModal={getModal}
            setModal={setModal}
            key={i}
            item={x}
            setModalItem={setModalItem}
          />
        ))}

        <Modal getModal={getModal} setModal={setModal} item={getModalItem} />
      </div>
    </div>
  );
}

export default App;
