import "./App.css";
import { useState } from "react";

import Header from "./components/header";
import Shop from "./components/Shop";

function App() {

  const buildings = [
    {
        name: "house",
        price: {
            gold: 10,
            stone: 20,
            wood: 50
        },
        image: "https://thumbs.dreamstime.com/b/house-pixel-art-isolated-home-pixels-white-background-136637846.jpg"    },
    {
        name: "castle",
        price: {
            gold: 100,
            stone: 200,
            wood: 500
        },
        image: "https://images.cdn4.stockunlimited.net/preview1300/pixel-castle_2021418.jpg"    },
    {
        name: "tent",
        price: {
            gold: 5,
            stone: 5,
            wood: 10
        },
        image: "https://preview.pixlr.com/images/800wm/100/1/1001509056.jpg"    },
]




  const [getGold, setGold] = useState(0);
  const [getWood, setWood] = useState(0);
  const [getStone, setStone] = useState(0);

  function addGold(){

    setGold(getGold+1)
  }
  function addStone(){

    setStone(getStone+1)
  }
  function addWood(){

    setWood(getWood+1)
  }





  return (
    <div className="App">
      < Header gold = {getGold} wood = {getWood} stone = {getStone}/>
      <div className="resourse">
        <img onClick={addGold} src="https://img.freepik.com/free-vector/pile-gold-bullion-with-pixel-art-style_475147-541.jpg?w=2000" alt="" />
        <img onClick={addWood} src="https://img.freepik.com/free-vector/wood-log-pixel-art-style_475147-1390.jpg" alt="" />
        <img  onClick={addStone} src="https://img.freepik.com/free-vector/sharp-stone-pixel-art-style_475147-1450.jpg" alt="" />
      </div>


      <div className="shopas"> <Shop/></div>



    </div>
  );
}

export default App;
