import "./App.css";
import { useState, useRef } from "react";

function App() {
  let inName = useRef();
  let inLocation = useRef();
  let inPhoto = useRef();

  const [color, setColor] = useState("white");

  function setUser() {
    let name = inName.current.value;
    let loc = inLocation.current.value;
    let image = inPhoto.current.value;
  }

  return (
    <div className="App">
      <div className="container">
        <div className="form">
          <input ref={inPhoto} type="url" placeholder="photo url" />
          <input ref={inName} type="text" placeholder="username" />
          <input ref={inLocation} type="text" placeholder="location" />
          <button onClick={setUser}>Add user</button>
        </div>
        <div className="display">
          <div className="objectas">
            <img
              src="https://thumbs.dreamstime.com/b/happy-smiling-geek-hipster-beard-man-cool-avatar-geek-man-avatar-104871313.jpg"
              alt=""
            />
            <div>Name:</div>
            <div>Location:</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
