import React from "react";
import Hash from "../components/Hash";
import { useRef, useState } from "react";

function GamePage({ word, setWord }) {
  const [allGuessed, setAllGuesed] = useState(false);
  let correctSpell = 0;
  let wrongSpell = 0;

  const inLetter = useRef();
  function guessing() {
    const guessing = {
      letter: inLetter.current.value,
    };

    const options = {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(guessing),
    };

    fetch("http://localhost:4000/guessing", options)
      .then((res) => res.json())
      .then((data) => {
        if (!data.error) {
          let tarpinis = [...word];
          tarpinis[data.index] = data.letter;
          setWord(tarpinis);
          correctSpell++;
        } else {
          wrongSpell++;
        }
        console.log(data);
      });
    console.log(correctSpell, word.length, wrongSpell);
    if (correctSpell === word.length) return setAllGuesed(true);
    inLetter.current.value = "";
  }
  return (
    <div className={"p-20 m-auto"}>
      <div className="all_hashes">
        {word.map((x, i) => (
          <Hash leter={x} key={i} />
        ))}
      </div>
      <div>
        <input
          ref={inLetter}
          type="text"
          name=""
          id=""
          placeholder="leter"
          maxLength="1"
        />
        <button onClick={guessing}>GUESS</button>
        {allGuessed && <button>START AGAIN</button>}
      </div>
    </div>
  );
}

export default GamePage;
