import "./App.css";
import { useState, useRef } from "react";

function App() {
  let inpValue = useRef();

  function setingColor() {
    let spalva = inpValue.current.value;
    setColor(spalva);
  }

  const [color, setColor] = useState("white");

  let Color = {
    backgroundColor: color,
  };

  return (
    <div className="App">
      <div className="container">
        <div className="form">
          <input type="text" placeholder="photo" />
        </div>
        <div className="display"></div>
      </div>
    </div>
  );
}

export default App;
