import { addUser } from "../features/users";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function NewInput() {
  const dispatch = useDispatch();
  // const test = useSelector((state) => state.users.value);
  const { selectedBox, setSelectedBox, indeksas, setIndeksas } =
    useContext(mainContext);

  const inName = useRef();

  function addToArray() {
    const setUser = {
      whoreservet: inName.current.value,
      index: indeksas,
    };
    dispatch(addUser(setUser));
    console.log(inName.current.value);
  }
  return (
    <div className="inputai">
      <input ref={inName} type="text" placeholder="username" />
      <button onClick={addToArray}>ADD</button>
    </div>
  );
}

export default NewInput;
