import "./App.css";

import { useState, useRef } from "react";

function App() {
  let inName = useRef();
  let inLocation = useRef();
  let inPhoto = useRef();

  const [getUser, setUser] = useState([]);

  function addUser() {
    const user = {
      name: inName.current.value,
      loc: inLocation.current.value,
      image: inPhoto.current.value,
    };
    setUser([...getUser, user]);
  }

  return (
    <div className="App">
      <div className="container">
        <div className="form">
          <input ref={inPhoto} type="url" placeholder="photo url" />
          <input ref={inName} type="text" placeholder="username" />
          <input ref={inLocation} type="text" placeholder="location" />
          <button onClick={addUser}>Add user</button>
        </div>
        <div className="display">
          {getUser.map((x) => (
            <div className="objectas">
              <img src={x.image} alt="" />
              <div>Name:{x.name}</div>
              <div>Location:{x.location}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
