import React from "react";
import { useSelector } from "react-redux";
import CartProduct from "../components/CartProduct";
import { useState } from "react";

function CartPage() {
  const cartProducts = useSelector((state) => state.products.value.products);
  const [filtered, setFilterd] = useState(cartProducts);

  let suma = 0;
  cartProducts.map((x) => {
    let tarpinis = x.price * x.quant;
    suma = suma + tarpinis;
  });

  return (
    <div>
      {cartProducts.map((x, i) => (
        <CartProduct
          key={i}
          item={x}
          filtered={filtered}
          setFilterd={setFilterd}
          index={i}
        />
      ))}
      <h1>TOTAL PRICE: {suma}</h1>
    </div>
  );
}

export default CartPage;
