import "./App.css";
import { useState } from "react";

// import Header from "./components/header";
// import Shop from "./components/Shop";

function App() {

  const buildings = [
    {
        name: "house",
        price: {
            gold: 10,
            stone: 20,
            wood: 50
        },
        image: "https://thumbs.dreamstime.com/b/house-pixel-art-isolated-home-pixels-white-background-136637846.jpg"    },
    {
        name: "castle",
        price: {
            gold: 100,
            stone: 200,
            wood: 500
        },
        image: "https://images.cdn4.stockunlimited.net/preview1300/pixel-castle_2021418.jpg"    },
    {
        name: "tent",
        price: {
            gold: 5,
            stone: 5,
            wood: 10
        },
        image: "https://preview.pixlr.com/images/800wm/100/1/1001509056.jpg"    },
]

const [getMyBuildings, setMyBuildings] = useState([]);

function push(item){
  console.log(item);
  let newArray = getMyBuildings
  newArray.push(item)
  setMyBuildings(newArray)

}

  return (
    <div className="App">

<div className="add">
   {getMyBuildings.map(x=> (
      <img src={x.image} alt="" />
    ))}
</div>

<div className="namukai">
  {buildings.map(x=> (
      <img onClick={()=>push(x)} src={x.image} alt="" />
    ))}
</div>


    </div>
  );
}

export default App;
