import React from "react";
import { useState, useRef } from "react";
import Question from "../components/Question";

function GamePage() {
  const image = useRef();
  const title = useRef();
  const ingredients = useRef();
  const prepSteps = useRef();
  const cost = useRef();

  function send() {
    const user = {
      username: usernameRef.current.value,
      email: emailRef.current.value,
      image: imageRef.current.value,
      password: passRef.current.value,
    };

    const options = {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(user),
    };

    fetch("http://localhost:4000/start", options)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
      });
  }

  return (
    <div className="start">
      <input ref={image} type="text" placeholder="image" />
      <button>add image</button>

      <input ref={title} type="text" placeholder="title" />

      <input ref={ingredients} type="text" placeholder="ingridients" />
      <button>add ingridient</button>

      <input ref={prepSteps} type="text" placeholder="prepSteps" />
      <button>Add step</button>

      <input ref={cost} type="text" placeholder="Cost" />

      <button onClick={send}>Send</button>
    </div>
  );
}

export default GamePage;
