import React from "react";
import { useNavigate } from "react-router-dom";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function Header() {
  const nav = useNavigate();
  function logoff() {
    nav("/");
    setlogedinUser(null);
  }
  const { logedinUser, setlogedinUser } = useContext(mainContext);
  return (
    <div className="header">
      <div className="posts">
        {logedinUser && (
          <button onClick={() => nav("/create")}>Create Post</button>
        )}
        {logedinUser && (
          <button onClick={() => nav("/allpost")}>All posts</button>
        )}
      </div>
      <div className="login">
        {logedinUser && <button onClick={logoff}>Logout</button>}
        {!logedinUser && (
          <div>
            <button onClick={() => nav("/")}>Login</button>
            <button onClick={() => nav("/register")}>Register</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default Header;
