import React from "react";
import { useRef, useEffect } from "react";

function ProfilePage({ logedUser, setLogedUser, setNupirkta }) {
  console.log(logedUser);
  const inPick = useRef();

  useEffect(() => {
    fetch("http://localhost:4000/checkBasket/" + logedUser.email)
      .then((res) => res.json())
      .then((data) => {
        // setNupirkta(data.nupirkta.length);
        console.log(data.nupirkta.length);
      });
  }, []);

  function changePic() {
    const urlas = {
      email: logedUser.email,
      img: inPick.current.value,
    };

    const options = {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(urlas),
    };

    fetch("http://localhost:4000/changePict", options)
      .then((res) => res.json())
      .then((data) => {
        // if (data.error) return setError(data.message);

        // setError(data.message);
        console.log(data);
        setLogedUser(data.user);
      });
  }
  return (
    <div className="profile">
      <div>
        <img src={logedUser.img} alt="" />
        <div>{logedUser.email}</div>
      </div>
      <div>
        <input ref={inPick} type="url" placeholder="new image URL" />
        <button onClick={changePic}>Change Picture</button>
      </div>
    </div>
  );
}

export default ProfilePage;
