    },
  ];

  let arr3 = [];
  for (let i = 0; i < 16; i++) {
    arr3.push({
      id: Date.now(),
      color: "white",
    });
  }

  const [getColor, setColor] = useState("white");

  const [getItems, setItems] = useState(arr3);

  //   function toBlack() {
