  //   }
  return (
    <div className="App">
      <div className="wrapper">
        {getItems.map((x, i) => (
          <Box
            key={i}
            id={x.id}
            setColor={setColor}
            getColor={getColor}
            color={x.color}
            setItems={setItems}
            getItems={getItems}
            item={x}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
