import React from "react";
import { useRef } from "react";
import Picture from "./Picture";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function Comp1() {
  // const { getImage, togle } = useContext(mainContext);
  // let bgColor = {
  //   flexDirection: togle ? "column" : "row",
  //   flexWrap: togle ? "" : "wrap",
  // };

  return (
    <div className="comp1">
      <div className="tollbar_set">
        <input type="text" placeholder="toolbar color" />
        <input type="number" placeholder="toolbar padding" />
        <input type="text" placeholder="URL for link1" />
        <input type="text" placeholder="URL for link2" />
        <input type="text" placeholder="URL for link3" />
      </div>

      <div className="header_set">
        <input type="url" placeholder="header bg image" />
        <input type="text" placeholder="header text" />
        <input type="number" placeholder="header hight" />
      </div>

      <div className="disebar_set">
        <input type="checkbox" />
        <label> sidebar On / OFF</label>
      </div>

      <div className="addpost">
        <input type="url" placeholder="post img" />
        <input type="text" placeholder="image text" />
      </div>
      <div className="maincontent">
        <input type="checkbox" />
        <label> row/column</label>
        <input type="text" placeholder="main bg color" />
      </div>
    </div>
  );
}

export default Comp1;
