import { useState, useRef, useEffect } from "react";

const Tasks = ({ getTaskas, index, setTask, getTask }) => {
  const [getStatus, setStatus] = useState(getTaskas.status);
  const [getButton, setButton] = useState(getTaskas.button);
  function completed() {
    setStatus(true);
    setButton("none");
    console.log(getTaskas.button);
  }
  function deleting() {
    let arr = getTask;
    setTask(arr.splice(index, 1));
    // console.log(removed);
  }

  return (
    <div
      className="vienastask"
      style={{
        backgroundColor: getStatus ? "green" : "",
      }}
    >
      <div>
        <h5>{getTaskas.date}</h5>
        <h2>{getTaskas.value}</h2>
      </div>
      <div className="buttons">
        <button
          onClick={completed}
          style={{
            display: getButton,
          }}
        >
          COMPLETED
        </button>
        <button onClick={deleting}>DELETE</button>
      </div>
    </div>
  );
};

export default Tasks;
