import React from "react";
import { useRef } from "react";

function UploadProductPage({ logedUser }) {
  const inPic = useRef();
  const inTitle = useRef();
  const inPrice = useRef();

  function uploading() {
    const product = {
      picture: inPic.current.value,
      title: inTitle.current.value,
      price: inPrice.current.value,
      email: logedUser.email,
    };

    const options = {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(product),
    };

    fetch("http://localhost:4000/uploadProduct", options)
      .then((res) => res.json())
      .then((data) => {
        // if (data.error) return setError(data.message);

        // setPosts(data.posts);
        // setError(data.message);
        console.log(data);
      });
  }

  return (
    <div className="upload d-flex flex-column">
      <input ref={inPic} type="url" placeholder="Product Image" />
      <input ref={inTitle} type="text" placeholder="Product Title" />
      <input ref={inPrice} type="number" placeholder="Product price" />

      <button onClick={uploading}>UPLOAD</button>
    </div>
  );
}

export default UploadProductPage;
