const Shop = ({id,gold,wood,stone,image, item, setMyBuildings,getMyBuildings, mygold, mywood, mystone}) => {

function addToarray(gold,mygold, wood,mywood,stone,mystone,item) {
    if(gold>mygold) {return console.log("no gold")}
    else 
    if(wood>mywood) {return console.log("no wood")};
    if(stone>mystone) {return console.log("no stone")};

    let build = getMyBuildings
    build.push(item)
    setMyBuildings(build)

    console.log(getMyBuildings);
}

  return <div id={id} onClick={()=>addToarray(gold,mygold,wood,mywood,stone,mystone, item)} className="building">
        <img src={image} alt="" />
        <div>Price:
            <div>Gold:{gold}</div>
            <div>Wood:{wood}</div>
            <div>Stone:{stone}</div>
        </div>

  </div>;
};

export default Shop;
