import "./App.css";

import { useState, useRef } from "react";

function App() {
  const papartis = [
    {
      image:
        "https://upload.wikimedia.org/wikipedia/commons/6/6b/Dryopteris_dilatata_855.JPG",
      ring: "flower",
    },
    {
      image:
        "https://upload.wikimedia.org/wikipedia/commons/6/6b/Dryopteris_dilatata_855.JPG",
      ring: "flower",
    },
    {
