        "https://upload.wikimedia.org/wikipedia/commons/6/6b/Dryopteris_dilatata_855.JPG",
      ring: "flower",
    },
    {
      image:
        "https://upload.wikimedia.org/wikipedia/commons/6/6b/Dryopteris_dilatata_855.JPG",
      ring: "flower",
    },
    {
      image:
        "https://upload.wikimedia.org/wikipedia/commons/6/6b/Dryopteris_dilatata_855.JPG",
      ring: "flower",
    },
    {
      image:
        "https://upload.wikimedia.org/wikipedia/commons/6/6b/Dryopteris_dilatata_855.JPG",
      ring: "flower",
    },
    {
      image:
        "https://upload.wikimedia.org/wikipedia/commons/6/6b/Dryopteris_dilatata_855.JPG",
      ring: "ring",
    },
    {
      image:
        "https://upload.wikimedia.org/wikipedia/commons/6/6b/Dryopteris_dilatata_855.JPG",
      ring: "flower",
    },
    {
      image:
        "https://upload.wikimedia.org/wikipedia/commons/6/6b/Dryopteris_dilatata_855.JPG",
      ring: "flower",
    },
  ].sort(() => Math.random() - 0.5);

  const [getScore, setScore] = useState(0);
  const [getWin, setWin] = useState(null);
  const [getArr, setArr] = useState(papartis);
  const [getClas, setClas] = useState("");

  function checkClass(event) {
    setScore(getScore + 1);
    console.log(event);
    console.log(event.target.currentSrc);

    if (event.target.className === "ring") {
      // event.target.currentSrc =
      //   "https://image.brilliantearth.com/media/diamond_ring_vto/8T/BE1D64_white_Round_top_75_carat.png";
      setWin("RADAI");
      setClas("ontop");
      console.log("RADAI");
    } else {
      event.target.classList.add("red_border");
    }
  }

  function onLoad() {}

  return (
    <div className="App">
      <div className="container">
        {getArr.map((x, i) => (
          <img
            onClick={(e) => checkClass(e)}
            key={i}
            className={x.ring}
            src="https://upload.wikimedia.org/wikipedia/commons/6/6b/Dryopteris_dilatata_855.JPG"
            alt=""
          />
        ))}
        <div className={getClas}></div>
      </div>
      <div className="score">
        <div className="result"> SCORE:{getScore}</div>
        <div className="restart">
          <button onClick={() => window.location.reload(false)}>
            PLAY AGAIN
          </button>
        </div>
      </div>
      <div className="notification">{getWin && <h1>{getWin}</h1>}</div>
    </div>
  );
}

export default App;
