import React from "react";

const ColoredBox = ({ color, getsum, setsum }) => {
  let bgColor = {
    backgroundColor: color.hex,
  };

  function addLetters(families) {
    let arr = families.join("").length;
    console.log(arr);
    setsum(getsum + arr);
  }

  return (
    <div
      onClick={() => addLetters(color.families)}
      className="boxes"
      style={bgColor}
    >
      {color.name} {color.families.length}
    </div>
  );
};

export default ColoredBox;
