import { createSlice } from "@reduxjs/toolkit";

export const userSlice = createSlice({
  name: "users",
  initialState: {
    value: [
      {
        username: "Andrius",
        useremail: "sa@as.lt",
        usercity: "Trakai",
        gender: "Male",
        hairColor: "Red",
        yearOld: 20,
        userimage:
          "https://cdn.britannica.com/91/181391-050-1DA18304/cat-toes-paw-number-paws-tiger-tabby.jpg?q=60",
      },
      {
        username: "Tomas",
        useremail: "sa@as.lv",
        usercity: "VIlnius",
        gender: "Male",
        hairColor: "Black",
        yearOld: 50,
        userimage:
          "https://www.cameraegg.org/wp-content/uploads/2013/03/Canon-EOS-100D-Rebel-SL1-Sample-Image.jpg",
      },
      {
        username: "Kestas",
        useremail: "sa@as.lv",
        usercity: "VIlnius",
        gender: "Female",
        hairColor: "Brown",
        yearOld: 30,
        userimage:
          "https://www.cameraegg.org/wp-content/uploads/2013/03/Canon-EOS-100D-Rebel-SL1-Sample-Image.jpg",
      },
      {
        username: "Kestas",
        useremail: "sa@as.lv",
        usercity: "VIlnius",
        gender: "Female",
        hairColor: "Black",
        yearOld: 30,
        userimage:
          "https://www.cameraegg.org/wp-content/uploads/2013/03/Canon-EOS-100D-Rebel-SL1-Sample-Image.jpg",
      },
    ],
  },
  reducers: {
    changeName: (state, action) => {
      state.value.username = action.payload;
    },
    changePicture: (state, action) => {
      state.value.userimage = action.payload;
    },
    addUser: (state, action) => {
      state.value.push(action.payload);
    },
    updateArr: (state, action) => {
      state.value = action.payload;
    },
  },
});
export const { changeName, changePicture, addUser, updateArr } =
  userSlice.actions;

export default userSlice.reducer;
