import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateArr } from "../features/users";
import { useContext } from "react";
import mainContext from "../context/mainContext";
import { useState } from "react";

function SingleUser({ box, index }) {
  const { setInput, selectedBox, setSelectedBox } = useContext(mainContext);
  const useriai = useSelector((state) => state.users.value);
  const dispatch = useDispatch();
  const [selected, setSelected] = useState(false);
  const [reserv, setReserv] = useState(box.reserved);

  function activeBox() {
    setInput(true);
    setSelected(true);
    setSelectedBox(box);
    console.log(selectedBox);
  }
  function backgroundas() {
    if (selected) return { background: "ligtGrey" };
    if (reserv) return "grey";
  }
  return (
    <div style={backgroundas} onClick={activeBox} className="onebox">
      {reserv && <h3>Reserved by:{box.whoreservet}</h3>}
    </div>
  );
}

export default SingleUser;
