import { useState, useRef, useEffect } from "react";

const Add = ({ getTask, setTast }) => {
  let inText = useRef;
  function addTask() {
    const taskas = {
      value: inText.current.value,
      completed: false,
      date: current.date,
    };
    setTask([...getTask, taskas]);
  }

  return (
    <div className="add">
      <input ref={inText} type="text" />
      <button onClick={addTask}>ADD</button>
    </div>
  );
};

export default Add;
