import { useState } from "react";

const Footer = () => {
  return (
    <div className="input">
      <input
        style={{ border: getStyles[0] }}
        ref={username}
        type="text"
        placeholder="username"
      />
      <input
        style={{ border: getStyles[1] }}
        ref={email}
        type="text"
        placeholder="email"
      />
      <input
        style={{ border: getStyles[2] }}
        ref={passOne}
        type="text"
        placeholder="pass one"
      />
      <input
        style={{ border: getStyles[2] }}
        ref={passTwo}
        type="text"
        placeholder="pass two"
      />
      <input
        style={{ border: getStyles[3] }}
        ref={location}
        type="text"
        placeholder="location"
      />

      {getError && <h1>{getError}</h1>}

      <button onClick={submitForm}>Submit</button>
    </div>
  );
};

export default Footer;
