import { useState, useRef, useEffect } from "react";

const Tasks = ({ getTask, setTask }) => {
  function completed() {
    getTask.status = false;
  }
  function deleting() {}

  return (
    <div className="tasks">
      {getTask.map((x, i) => (
        <div key={i} className="vienastask" style={{ background: "green" }}>
          <div>
            <h5>{x.date}</h5>
            <h2>{x.value}</h2>
          </div>
          <div className="buttons">
            <button onClick={completed}>COMPLETED</button>
            <button onClick={deleting}>DELETE</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Tasks;
