import { createSlice } from "@reduxjs/toolkit";

export const userSlice = createSlice({
  name: "users",
  initialState: {
    value: {
      reservations: [
        {
          id: 111,
          reserved: false,
          whoreservet: "",
          selected: false,
        },
        {
          id: 222,
          reserved: false,
          whoreservet: "",
          selected: false,
        },
        {
          id: 333,
          reserved: true,
          whoreservet: "Andrius",
          selected: false,
        },
        {
          id: 444,
          reserved: false,
          whoreservet: "",
          selected: false,
        },
        {
          id: 555,
          reserved: false,
          whoreservet: "",
          selected: false,
        },
        {
          id: 666,
          reserved: false,
          whoreservet: "",
          selected: false,
        },
        {
          id: 777,
          reserved: false,
          whoreservet: "",
          selected: false,
        },
        {
          id: 888,
          reserved: false,
          whoreservet: "",
          selected: false,
        },
        {
          id: 999,
          reserved: false,
          whoreservet: "",
          selected: false,
        },
      ],
    },
  },
  reducers: {
    changeSelected: (state, action) => {
      state.value.reservations[action.payload.index].selected =
        action.payload.selected;
    },
    addUser: (state, action) => {
      state.value.reservations[action.payload.index].whoreservet =
        action.payload.whoreservet;
      state.value.reservations[action.payload.index].reserved =
        action.payload.reserved;
    },
  },
});
export const { changeSelected, addUser } = userSlice.actions;

export default userSlice.reducer;
