import { useState, useRef, useEffect } from "react";

const Items = ({ item, getInventor, setInventor, setGold, getGold }) => {
  function addToInventor() {
    setInventor([...getInventor, item]);
    setGold(getGold - item.price);
    console.log(getInventor);
  }

  //   useEffect(() => {
  //     if (getGold < item.price) {
  //       return console.log("not enought GOLD");
  //     }
  //   }, [getGold]);

  return (
    <div className="item">
      <img src={item.image} alt="" />
      <div className="description">
        {item.title ? <p>{item.title}</p> : null}
        <p>Price:{item.price}</p>
        {item.maxDamage ? <p>Max demage: {item.maxDamage}</p> : null}

        {item.energyPerHit ? <p>Energy per hit: {item.energyPerHit}</p> : ""}
        {item.effects ? (
          <div className="effects">
            Effect:{" "}
            {item.effects.map((x, i) => (
              <p key={i}>{x}</p>
            ))}
          </div>
        ) : null}
        {item.effect ? <div>Effect: helth: {item.effect.health}</div> : null}
      </div>
      <button onClick={addToInventor}>BUY</button>
    </div>
  );
};

export default Items;
