import "./App.css";

import { useState, useRef } from "react";
// import Images from "./components/Image";

function App() {
  let inUser = useRef();
  let inEmail = useRef();
  let inPass1 = useRef();
  let inPass2 = useRef();
  let inLocation = useRef();

  const [getUser, setUser] = useState([]);
  const [getError, setError] = useState([false]);

  function Validation() {
    if (inUser.current.value.length > 20 || inUser.current.value.length < 3) {
      return setError(true);
    } else {
      setError(false);
    }

    console.log("wrong");
    // const user = {
    //   photo: inPhoto.current.value,
    //   repeat: inRepeat.current.value,
    // };
    // setPhoto([...getPhoto, image]);
  }
  const inputStyle = {
    border: getError ? "2px solid red" : "1px solid black",
  };

  return (
    <div className="App">
      <div className="container">
        <div className="form">
          <input
            ref={inUser}
            type="text"
            placeholder="username"
            style={inputStyle}
          />
          <input
            ref={inEmail}
            type="email"
            placeholder="email"
            style={inputStyle}
          />
          <input
            ref={inPass1}
            type="text"
            placeholder="password"
            style={inputStyle}
          />
          <input
            ref={inPass2}
            type="text"
            placeholder="confirm password"
            style={inputStyle}
          />
          <input
            ref={inLocation}
            type="text"
            placeholder="location"
            style={inputStyle}
          />
          <div>ERROR</div>
          <button onClick={Validation}>Repeat</button>
        </div>
      </div>
    </div>
  );
}

export default App;
