          <input ref={inPass1} type="text" placeholder="password" />
          <input ref={inPass2} type="text" placeholder="confirm password" />
          <input ref={inLocation} type="text" placeholder="location" />
          <div>ERROR</div>
          <button onClick={Validation}>Repeat</button>
        </div>
      </div>
