import React from "react";
import { useRef } from "react";

function Comp1() {
  const inColor = useRef();
  function saveColor() {
    const spalva = {
      color: inColor.current.value,
    };

    const options = {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(spalva),
      credentials: "include",
    };

    fetch("http://localhost:4000/session1", options)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
      });
  }
  return (
    <div>
      <input ref={inColor} type="text" placeholder="color" />
      <button onClick={saveColor}>Save Color</button>
    </div>
  );
}

export default Comp1;
