  useEffect(() => {
    if (getCounter <= 10) {
      setHight(getCounter * 10);
    }

    if (getCounter < 6) setColor("#78c953");
    if (getCounter > 6 && getCounter < 9) setColor("yellow");
    if (getCounter > 9) setColor("red");
  }, [getCounter]);

  return (
    <div className="progressBar">
