import React from "react";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function Inventor({ item, index, parameter }) {
  const {
    getInventor,
    setInventor,
    setGold,
    getGold,

    setFightItem,
  } = useContext(mainContext);

  function removeFromInventor() {
    let arr = getInventor;
    arr.splice(index, 1);

    setInventor([...arr]);

    setGold(getGold + item.price / 2);
  }

  function arenFightitem() {
    if (parameter === "modal") {
      setFightItem(item);
    } else {
      console.log("not from modal");
    }
  }

  return (
    <div onClick={arenFightitem} className="item">
      <img src={item.image} alt="" />
      <div>Price: {item.price}</div>
      {item.maxDamage ? <p>Max demage: {item.maxDamage}</p> : null}

      {item.title ? <p>Title: {item.title}</p> : null}
      {parameter && <button onClick={() => removeFromInventor()}>SELL</button>}
    </div>
  );
}

export default Inventor;
