import React from "react";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";

function check(param) {
  const filtered = users.filter((x) => x.username === param);
  if (filtered.length === 0) {
    return <div className="test">"USER DO NOT EXIST"</div>;
  } else {
    return (
      <div className="test">
        <div>User name: {filtered[0].username}</div>
        <div>Name: {filtered[0].name}</div>
        <div>Email: {filtered[0].email}</div>
        <div>Phone: {filtered[0].phone}</div>
        <div>WEB: {filtered[0].website}</div>
      </div>
    );
  }
}

function UserPage() {
  const user = useParams();
  const [getPost, setPost] = useState([]);
  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((res) => res.json())
      .then((data) => {
        setPost(data);
      });
  }, []);

  console.log(getPost);

  return (
    <div>
      <div className="divas">{check(user.id)}</div>
    </div>
  );
}

export default UserPage;
