import React from "react";
import { useState, useRef } from "react";
import Question from "../components/Question";

function GamePage() {
  const usernameRef = useRef();
  const emailRef = useRef();
  const imageRef = useRef();
  const passRef = useRef();

  function send() {
    const user = {
      username: usernameRef.current.value,
      email: emailRef.current.value,
      image: imageRef.current.value,
      password: passRef.current.value,
    };

    const options = {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(user),
    };

    fetch("http://localhost:4000/start", options)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
      });
  }

  return (
    <div className="start">
      <input ref={usernameRef} type="text" placeholder="image" />
      <button>add image</button>

      <input ref={emailRef} type="text" placeholder="title" />

      <input ref={imageRef} type="text" placeholder="ingridients" />
      <button>add ingridient</button>

      <input ref={passRef} type="text" placeholder="prepSteps" />
      <button>Add step</button>

      <input ref={passRef} type="text" placeholder="Cost" />

      <button onClick={send}>Send</button>
    </div>
  );
}

export default GamePage;
