    if (counter > 25 && counter < 40) setColor("yellow");
    if (counter > 40) setColor("red");
    if (counter > 50) setButton("none");
  }, [counter]);
  return (
    <div className="progressBar">
      <div className="progressWrapper">
        <div
          style={{ height: getHight + "px", backgroundColor: getColor }}
        ></div>
