import React from "react";
import { useNavigate } from "react-router-dom";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function AllpostPage() {
  const { posts, logedinUser, setSelectedPost } = useContext(mainContext);
  const nav = useNavigate();

  function openPost(id, item) {
    nav("/singlepost/" + id);
    console.log("test");
    setSelectedPost(item);
  }

  return (
    <div className="posts">
      {posts.map((x, i) => (
        <div onClick={() => openPost(x.id, x)} key={i} className="singlepost">
          <img src={x.image} alt="" />
          <div>{x.title}</div>
          <div>{x.location}</div>
          <div>{logedinUser.email}</div>
          <div>Coments:{x.coments.length}</div>
        </div>
      ))}
    </div>
  );
}

export default AllpostPage;
