import React from "react";
import { useParams } from "react-router-dom";
import { useRef, useState } from "react";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function ProfilePage() {
  const { logedinUser, setlogedinUser } = useContext(mainContext);
  let inPic = useRef();

  function changePicture() {
    const user = { ...logedinUser };
    user.picture = inPic.current.value;

    setlogedinUser(user);
  }
  return (
    <div className="user">
      <img src={logedinUser.picture} alt="" />
      <h1>{logedinUser.email}</h1>
      <input ref={inPic} type="url" />
      <button onClick={changePicture}> CHANGE PICTURE </button>
    </div>
  );
}

export default ProfilePage;
