import React from "react";
import { useRef } from "react";
import { useSelector, useDispatch } from "react-redux";

function Input() {
  const dispatch = useDispatch();
  const inUrl = useRef();
  const inName = useRef();
  function addUser() {
    const newUser = {
      photo: inUrl.current.value,
      username: inName.current.value,
    };
    dispatch(addUser(newUser));
  }
  return (
    <div className="inputs">
      <input ref={inUrl} type="Url" placeholder="Photo url" />
      <input ref={inName} type="text" placeholder="Username" />
      <button on onClick={addUser}>
        {" "}
        Add User{" "}
      </button>
    </div>
  );
}

export default Input;
